                    <div className='inst-btns'><button className='instruction-btn3'>2</button><p>Marked</p><br /></div><br />
                    <div className='inst-btns'><button className='instruction-btn4'>1</button><p>Not Visited</p></div>
                </div>
            </div>

        </div>
    )

}

