import React, { useEffect, useRef, useState } from 'react';
import './Home.css'
// import { Link } from 'react-router-dom'

// import Paper from '../Paper/Paper'
import { useNavigate } from 'react-router-dom'



const Home = ({ seconds }) => {

     const [countdown, setCountdown] = useState(seconds);
    const timerId = useRef();

    useEffect(() => {
        if (countdown <= 0) {
            clearInterval(timerId.current);
            alert("End");
        }
    }, [countdown]);
    const navigate = useNavigate();
     const startCountdown = () => {
         timerId.current = setInterval(() => {
             setCountdown((prev) => prev - 1);
         }, 1000);
         navigate('/Paper');

     };

  return (
      <div className='home-page'>
          <div className='header-div'><h1 className='heading'>Start Quiz</h1></div>





          <div className='btn-links'>
              <div className='play'><button onClick={startCountdown} className='play-btn' >Play</button></div>
              {/* to='/Paper' element={<Paper />}  */}
              {/* <div className='log-sign-btns'>
                  <Link className='login-btn' to='/loginPage' element={<loginPage />}>Login</Link>
                  <Link className='signup-btn' to='/signupPage' element={<signupPage />}>SignUp</Link>
              </div> */}
          </div>


      </div>
  )
}

export default Home
