import React, {useState} from 'react'




const Paper = () => {

    const [activeQuestion, setActiveQuestion] = useState()
    const [selectedAnswer, setSelectedAnswer] = useState('')

    const onClickNext = () => {
        setActiveQuestion((prev) => prev + 1)
        setResult((prev) =>
            selectedAnswer
                ? {
                    ...prev,
                    score: prev.score + 5,
                    correctAnswers: prev.correctAnswers + 1,
                }
                : { ...prev, wrongAnswers: prev.wrongAnswers + 1 }
        )
    }
    const { questions } = Paper
    const { question, choices } = questions[activeQuestion]

    const onAnswerSelected = (answer) => {
        setSelectedAnswerIndex(index)
        if (answer === correctAnswer) {
            setSelectedAnswer(true)

        } else {
            setSelectedAnswer(false)

        }
    }


    const [selectedAnswerIndex, setSelectedAnswerIndex] = useState(null)



  return (
    <div>
          <div className="quiz-container">
              <h1>Quiz</h1>
              <h2>{question}</h2>
              <ul>
                  {choices.map((answer, index) => (
                      <li onClick={() => onAnswerSelected(answer, index)} key={answer} className={selectedAnswerIndex === index ? 'selected-answer' : null}>
                          {answer}
                      </li>
                  ))}
              </ul>
              <button onClick={onClickNext} disabled={selectedAnswerIndex === null}>{activeQuestion === questions.length - 1 ? 'Finish' : 'Next'}</button>
          </div>
    </div>
  )
}

export default Paper
