import React, { useEffect, useState } from 'react';
import quiz from '../../Data/quesAns'
import './Paper.css'



// Defining Timer code 
const formatTime = (time) => {
    let minutes = Math.floor(time / 60);
    let seconds = Math.floor(time - minutes * 60);

    if (minutes < 10) minutes = '0' + minutes;
    if (seconds < 10) seconds = '0' + seconds;
    return minutes + ':' + seconds;
}; // End of Defining Timer code 

const Paper = ({ seconds }) => {
    const [activeQuestion, setActiveQuestion] = useState(0)
    // const [selectedAnswer, setSelectedAnswer] = useState('') 
     const [selectedAnswers, setSelectedAnswers] = useState(new Array(quiz.questions.length).fill(''));
    const [showResult, setShowResult] = useState(false)
    const [result, setResult] = useState({
        score: 0,
        correctAnswers: 0,
        wrongAnswers: 0,
    })

    const { questions } = quiz
    const { question, choices, correctAnswer } = questions[activeQuestion]
    const onClickNext = () => {
        setResult((prev) =>
            selectedAnswers[activeQuestion] === correctAnswer
                ? {
                    ...prev,
                    score: prev.score + 5,
                    correctAnswers: prev.correctAnswers + 1,
                }
                : { ...prev, wrongAnswers: prev.wrongAnswers + 1 }
        );
        if (activeQuestion !== questions.length - 1) {
            setActiveQuestion((prev) => prev + 1);
        } else {
            // setActiveQuestion(0);
            setShowResult(true);
        }

    };
    const onAnswerSelected = (answer) => {
        // Update the selected answer for the current question
        const updatedSelectedAnswers = [...selectedAnswers];
        updatedSelectedAnswers[activeQuestion] = answer;
        setSelectedAnswers(updatedSelectedAnswers);
    };
    const clearResponse = () => {
        // Clear the response for the current question
        const updatedSelectedAnswers = [...selectedAnswers];
        updatedSelectedAnswers[activeQuestion] = '';
        setSelectedAnswers(updatedSelectedAnswers);
    };

    const goToPreviousQuestion = () => {
        // Move to the previous question
        if (activeQuestion > 0) {
            setActiveQuestion((prev) => prev - 1);
        }
    };

    // const onAnswerSelected = (answer) => {
    //     setSelectedAnswer(answer);
    // }

    const addLeadingZero = (number) => (number > 9 ? number : `0${number}`)
    const [countdown, setCountDown] = useState(seconds);
    useEffect(() => {
        const timerId = setInterval(() => {
            setCountDown((prevCountDown) =>
                prevCountDown - 1
            );
        }, 1000)
        return () => {
            clearInterval(timerId);
        };
    }, [])

    useEffect(() => {
        if (countdown <= 0) {
            setShowResult(true);
            // clearInterval(timerId.current);
            // alert("End");
        }
    }, [countdown]);


    return (


        <div className="quiz-container">
            {!showResult ? (
                <div>
                    <div className='single'>

                    </div>
                    <div className='timer'>
                        <h3>Time Left: {formatTime(countdown)}</h3>
                    </div>
                    <div>
                        <span className="active-question-no">Question No. {addLeadingZero(activeQuestion + 1)}</span>
                        <span className="total-question"> of {addLeadingZero(questions.length)}</span>
                    </div>

                    <h2><img src={question} alt="question" /></h2>
                    <ul>
                        {choices.map((answer, index) => (
                            <li key={answer}>
                                <input type="radio"
                                    id={answer}
                                    name='answer'
                                    value={answer}
                                    checked={selectedAnswers[activeQuestion] === answer}
                                    onChange={() => onAnswerSelected(answer)} />


                                <label htmlFor={answer}>
                                    <img src={answer} alt="answer" />
                                </label>
                            </li>
                        ))}
                    </ul>
                    <div className="flex-right">
                        <button onClick={goToPreviousQuestion} disabled={activeQuestion === 0}>
                            Previous
                        </button>
                        <button onClick={onClickNext} disabled={!selectedAnswers[activeQuestion]}>
                            {activeQuestion === questions.length - 1 ? 'Submit' : 'Save & Next'}
                        </button>
                        <button onClick={clearResponse}>Clear Response</button>
                    </div>
                </div>
            ) : (
                <div className="result">
                    <h3>Result</h3>
                    <p>
                        Total Question: <span>{questions.length}</span>
                    </p>
                    <p>
                        Total Score:<span> {result.score}</span>
                    </p>
                    <p>
                        Correct Answers:<span> {result.correctAnswers}</span>
                    </p>
                    <p>
                        Wrong Answers:<span> {result.wrongAnswers}</span>
                    </p>
                </div>
            )}
        </div>
    )
}

export default Paper
